import React, {useReducer} from 'react';
import {reducer, initialState} from '../modules/store/reducer';
export const userContext = React.createContext('dark');
export const stateContext = React.createContext();
export const stateManager = ({children}) => {
	return <stateContext.Provider value={useReducer(reducer, initialState)}>{children}</stateContext.Provider>;
};
