import React, {useContext, useEffect} from 'react';
import Item from './Item';
import NoItem from '../../Partials/Filter/NoItem';
import Filter from '../../Partials/Filter';
import FilterSign from '../../Partials/Filter/FilterSign';
import {list} from '../../Partials/style';
import Pagination from '../../Partials/Pagination';
import contextManager from '../../Utility/contextManager';
import Axios from '../../Ajax/Axios';
const Products = () => {
	const {state, dispatch} = useContext(contextManager);
	const axios = new Axios();
	const filter = 'all';
	useEffect(() => {
		if (state.items.length > 0 && state.items === undefined) {
			return (
				state.items
					// .filter((product) => {
					// 	if (filter === 'all' || filter === 'filter') return true;
					// else {
					// 	const filterType = filter === 'income' ? 2 : 1;
					// 	return product.status === filterType;
					// }
					// })
					.map((product) => {
						axios
							.post('product/add', product)
							.then((res) => {
								console.log(res.data.item);
								dispatch(res.data.item);
							})
							.catch((err) => console.log(err));
						return <Item {...product} />;
					})
			);
		} else {
			return <NoItem col="6" />;
		}
	}, [state.items.lenngth]);

	const filterHandler = (filter) => {};
	const titles = {
		all: 'همه',
		income: 'درآمد',
		cost: 'هزینه',
	};

	const changeCurrentPage = (e, page) => {
		e.preventDefault();
	};
	const THeadTitle = {
		id: {title: 'ID', clickable: true},
		category: {title: 'دسته بندی'},
		price: {title: 'قیمت'},
		date: {title: 'تاریخ'},
		status: {title: 'وضعیت'},
		opreation: {title: 'عملیات'},
	};

	const showFilter = filter === 'filter' ? <Filter filtering={filterHandler} title={titles} /> : <FilterSign filtering={filterHandler} />;

	return (
		<div className="row">
			<div className="col">
				<div className="card" style={list}>
					{/* <div className="card-body">
							<div className="actions">
								<button onClick={(e) => render('addList')} className="btn btn-outline-success btn-icon m-l-5">
									<i className="material-icons">note_add</i>
									افزودن وظیفه
								</button>
							</div>
						</div> */}
					{showFilter}
					<div className="card-header">لیست محصولات</div>
					<div className="card-body">
						{/* generate table dynamicly */}
						{/* <Table theadData={THeadTitle} tbodyData={products} /> */}
						<table className="table table-bordered table-hover table-striped text-center">
							<thead>
								<tr>
									<th>ID</th>
									<th>دسته بندی</th>
									<th>قیمت</th>
									<th>تاریخ</th>
									<th>وضعیت</th>
									<th>عملیات</th>
								</tr>
							</thead>
							<tbody>{}</tbody>
						</table>
						<br />
						<div>
							<Pagination totalPage={5} currentPage={1} changeCurrentPage={changeCurrentPage} />
						</div>
					</div>
				</div>
			</div>
		</div>
	);
};
export default Products;
