const products = localStorage.getItem('product');
const product = JSON.parse(products);
export const initialState = {items: []};
export const reducer = (state, action) => {
	switch (action.type) {
		case 'add':
			addHandler(state, action);
			break;
		default:
			break;
	}
	return getStateFromLocalStorage(initialState, product);
};
const getStateFromLocalStorage = (newState, data) => {
	return {items: [...newState.items, data]};
};
const addHandler = (state, action) => {
	return {...state, items: [...state.items, action.payload]};
};
