import React from 'react';
import {list} from '../style';

const Filter = ({filtering, title}) => {
	return (
		<div class="d-flex flex-row-reverse align-items-start">
			<div class="p-0 rounded-right" style={list}>
				<button onClick={() => filtering('all')} style={list} type="button" className="btn btn-sm btn-outline-info border-0 m-0">
					{title.all}
				</button>
				<button onClick={() => filtering('cost')} style={list} type="button" className="btn btn-sm btn-outline-info border-0 m-0">
					{title.cost}
				</button>
				<button onClick={() => filtering('income')} style={list} type="button" className="btn btn-sm btn-outline-info border-0 m-0">
					{title.income}
				</button>
			</div>
		</div>
	);
};

export default Filter;
