import React, {useReducer} from 'react';
import {reducer, initialState} from '../modules/store/reducer';
export const userContext = React.createContext('dark');
export const stateContext = React.createContext();
// eslint-disable-next-line react-hooks/rules-of-hooks
export const stateManager = ({children}) => <stateContext.Provider value={useReducer(reducer, initialState)}>{children}</stateContext.Provider>;
