import React, {useState} from 'react';
import {Switch, Route} from 'react-router-dom';
import SideBar from './SideBar';
import Loader from '../Partials/Loader';
import Header from '../Partials/Header';
import routes from './Router/routes';
import {StateManager} from './store/context/ContextManager';
import Modal from './Modal';

const Apps = () => {
	let [is_loading, setIs_loading] = useState(false);
	let [type, setType] = useState('all');

	const componentHandler = (type) => setType(type);
	const loading = is_loading ? <Loader /> : null;
	const components = () => {
		return (
			<Switch>
				{routes.map(({path, exact, component: Component}, index) => (
					<Route key={index} path={path} exact={exact} render={(props) => <Component {...props} products={[]} courses={[]} item={[]} />} />
				))}
			</Switch>
		);
	};
	return (
		<StateManager>
			<div id="wrapper" className="rtl">
				<div id="container">
					<Header />
					<Modal />
					<SideBar render={componentHandler} />
					<div class="dashboard-wrapper mt-3">
						<div class="dashboard-ecommerce ">
							<div class="container-fluid dashboard-content  ">
								{loading}
								{components()}
							</div>
						</div>
					</div>
				</div>
			</div>
		</StateManager>
	);
};

export default Apps;

