import {actions} from '../actions';

export const initialState = {
	users: {items: [], messages: 'sentiment_very_dissatisfied'},
	products: {items: []},
	courses: {items: []},
};
export const reducer = (state = initialState, action) => {
	let result = state;

	switch (action.type) {
		// case actions.FETCH_USER:
		// 	result = {...state, items: [...state.items, ...action.payload]};
		// 	break;
		case actions.FETCH_USER_SUCCESS:
			result = {...state, items: action.payload.users, messages: action.payload.messages};
			break;
		case actions.FETCH_USER_FAILED:
			result = {...state, items: [...state.items, action.payload.messages]};
			break;
		default:
			result = state;
			break;
	}
	return result;
};

