import React from 'react';
import Modal from './Modal';
import {Switch, Route} from 'react-router-dom';
import {connect} from 'react-redux';
import actions from '../actions';
const Modals = ({modal, updateModal}) => {
	const componentHandler = () => {
		return (
			<Switch>
				<Route path={modal.path} exact component={modal.component} />
			</Switch>
		);
	};

	return (
		<div style={{'direction': 'ltr', 'text-align': 'left'}}>
			<Modal show={modal.show} body={componentHandler()} closeModal={() => updateModal({show: true})} />
			<button onClick={(e) => updateModal({show: true})} style={{color: 'white'}}>
				List of Insta
			</button>
		</div>
	);
};

const mapState = (state) => ({modal: state.modal.items, messages: state.users.messages, errorMessages: state.users.errorMessages});

const mapDispatch = (dispatch) => {
	return {
		updateModal: (payload) => {
			dispatch({
				type: actions.FETCH_USER,
				payload,
			});
		},
	};
};

export default connect(mapState, mapDispatch)(Modals);
