import React, {useState, useEffect} from 'react';

const UserProfile = (props) => {
	const userId = props.match.params.id;
	const [items, setItems] = useState([]);
	useEffect(() => {
		fetch(`https://jsonplaceholder.ir/users/${userId}`)
			.then((res) => res.json())
			.then((dataFetch) => {
				setItems(dataFetch);
				// localStorage.setItem('table', JSON.stringify(dataFetch));
			});
	}, []);

	const {email, name, avatar, phone, address} = items;

	const userRender = () => {
		return (
			<tr>
				<td className="dark">
					<img style={{width: '60px', height: '60px'}} src={avatar} alt="" />
				</td>
				<td className="dark">{name}</td>
				<td className="dark">{email}</td>
				<td className="dark">{address}</td>
				<td className="dark">{phone}</td>
			</tr>
		);
	};
	return (
		<table className="table table-bordered table-hover table-striped text-center" style={{width: '70%', height: '50%'}}>
			<tbody>{userRender()}</tbody>
		</table>
	);
};

export default UserProfile;
