import React, {useState, useEffect} from 'react';
import Phone from '../../Partials/Phone/Phone';

const UserProfile = (props) => {
	const userId = props.match.params.id;
	const [items, setItems] = useState([]);
	useEffect(() => {
		fetch(`https://jsonplaceholder.ir/users/${userId}`)
			.then((res) => res.json())
			.then((dataFetch) => {
				setItems(dataFetch);
				// localStorage.setItem('table', JSON.stringify(dataFetch));
			});
	}, []);

	const {email, name, avatar, phone, company} = items;

	const userRender = () => {
		return (
			<div>
				<div className="ark">
					<img style={{width: '60px', height: '60px'}} src={avatar} alt="" />
				</div>
				<div className="dark">{name}</div>

				<div className="dark">{email}</div>
				<div className="dark">{company}</div>
				<div className="dark">
					<Phone number={phone} />
				</div>
			</div>
		);
	};
	return <div style={{width: '70%', height: '50%'}}>{userRender()}</div>;
};

export default UserProfile;
