import React from 'react';
import {Switch, Route} from 'react-router-dom';
import {connect} from 'react-redux';
import {actions} from '../actions';
import Register from '../auth/Register';
import Login from '../auth/Login';
const Modals = ({modal, updateModal}) => {
	const currentComponent = (modal) => {
		let result = '';
		switch (modal.component) {
			case 'Register':
				result = Register;
				break;
			case 'Login':
				result = Login;
				break;

			default:
				break;
		}
		return result;
	};
	const componentHandler = () => {
		return (
			<Switch>
				<Route component={currentComponent(modal)} />
			</Switch>
		);
	};

	return (
		<div style={{'direction': 'ltr', 'text-align': 'left', 'zIndex': 9999}}>
			<div className={`modal-wrapper modalStatus-${modal.status === true ? 'show' : 'hidden'}`}>
				<button className="btn btn-danger" onClick={(e) => updateModal({status: false})}>
					X
				</button>
				<div className="modal-overlay">{componentHandler()}</div>
			</div>
		</div>
	);
};

const mapState = (state) => ({modal: state.modal.items});
const mapDispatch = (dispatch) => {
	return {
		updateModal: (payload) => {
			dispatch({
				type: actions.UPDATE_MODAL,
				payload,
			});
		},
	};
};

export default connect(mapState, mapDispatch)(Modals);
