import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import userContext from '../../Utility/userContext';

const Td = ({metaData, data}) => {
	const [filter, setFilter] = useState('asce');
	const {match, history, location} = metaData;
	const url = match.url;
	const search = location.search;

	const query = new URLSearchParams(search);
	const filterType = query.get('id');
	const {id, name, avatar} = data;
	const changeFilterType = () => setFilter(filterType);
	return (
		<userContext.Consumer>
			<button onClick={() => changeFilterType()}>type</button>
			{(value) => (
				<tr>
					<td className={value}>
						<img style={{width: '60px', height: '60px'}} src={avatar} alt="" />
					</td>
					<td className={value}>{name}</td>
					<td className={value}>
						<Link to={`${url}/${id}`}>
							<i className="material-icons">link</i>
						</Link>
					</td>
				</tr>
			)}
		</userContext.Consumer>
	);
};

export default Td;
