import React, {useReducer} from 'react';

const Test = () => {
	const initialState = {items: []};
	const reducer = (state, action) => (action.type === 'add' ? {...state, items: [...state.items, action.payload]} : state);
	const [state, dispatch] = useReducer(reducer, initialState);
	const renderItems = state.items.map((item) => <h1>{item.name}</h1>);
	return (
		<div>
			<button className="btn btn-info m-t-50" onClick={() => dispatch({type: 'add', payload: {name: 'ali'}})}>
				action
			</button>
			<div className="card">{renderItems}</div>
		</div>
	);
};

export default Test;

