import React from 'react';
import {Switch, Route} from 'react-router-dom';
import {connect} from 'react-redux';
import {actions} from '../actions';
const Modals = ({modal, updateModal}) => {
	let status = true;
	const componentHandler = () => {
		return (
			<Switch>
				<Route path={modal.path} exact component={modal.component} />
			</Switch>
		);
	};

	return (
		<div style={{'direction': 'ltr', 'text-align': 'left', 'zIndex': 9999}}>
			<div className={`modal-wrapper modalStatus-${status === true ? 'show' : 'hidden'}`}>
				<button className="btn btn-danger" onClick={(e) => updateModal({status: false})}>
					X
				</button>
				<div className="modal-overlay">{componentHandler()}</div>
			</div>
		</div>
	);
};

const mapState = (state) => ({modal: '', status: ''});
const mapDispatch = (dispatch) => {
	return {
		updateModal: (payload) => {
			dispatch({
				type: actions.UPDATE_MODAL,
				payload,
			});
		},
	};
};

export default connect(mapState, mapDispatch)(Modals);
