import React, {useContext} from 'react';
import {Link} from 'react-router-dom';
import {UserContext} from '../store/context/ContextManager';
import {connect} from 'react-redux';
import {actions} from '../actions';
const Td = ({metaData, data, updateUser, deleteUser, id}) => {
	const {match} = metaData;
	const url = match.url;
	const {name, avatar} = data;
	const {theme} = useContext(UserContext);

	return (
		<tr>
			<td className={theme}>
				<img style={{width: '60px', height: '60px'}} src={avatar} alt="" />
			</td>
			<td id={`user-${id}`} className={theme}>
				{name}
			</td>

			<td className={theme}>
				<Link to={`${url}`}>
					<i className="material-icons">link</i>
				</Link>
			</td>

			<td
				className={theme}
				onClick={(e) => {
					updateUser({
						id: `user-${id}`,
						newName: document.querySelector(`#user-${id}`).value,
					});
				}}>
				<span class="material-icons">edit</span>
			</td>
			<td className={theme}>
				<span class="material-icons">delete</span>
			</td>
		</tr>
	);
};

const mapState = (state) => ({userState: state.users.items, messages: state.users.messages, errorMessages: state.users.errorMessages});

const mapDispatch = (dispatch) => {
	return {
		updateUser: (payload) => {
			dispatch({
				type: actions.UPDATE_USER,
				payload,
			});
		},
		deleteUser: (id) => {
			dispatch({
				type: actions.DELETE_USER,
				payload: id,
			});
		},
	};
};

export default connect(mapState, mapDispatch)(Td);
