import {actions} from '../../actions';

// items: {path:'/',component:'Login',status:false}

const reducer = (modalState, action) => {
	let result = modalState;
	switch (action.type) {
		case actions.UPDATE_MODAL:
			result = {...modalState, items: {...modalState.items, ...action.payload}};
			break;
		// case actions.UPDATE_MODAL_SUCCESS:
		// 	result = {...modalState, items: action.payload.courses, messages: action.payload.messages};
		// 	break;
		// case actions.UPDATE_MODAL_FAILED:
		// 	result = {...modalState, items: [...modalState.items, action.payload.messages]};
		// 	break;
		default:
			result = modalState;
			break;
	}
	return result;
};

export default reducer;
