import {all} from 'redux-saga/effects';
import {fetchUserWatcher, registerUserWatcher, updateUserWatcher} from './saga';
export default function* middlewares() {
	yield all([fetchUserWatcher(), registerUserWatcher(), updateUserWatcher()]);
}
