import React, {useState, useEffect} from 'react';
import Phone from '../../Partials/Phone/Phone';

const UserProfile = (props) => {
	const userId = props.match.params.id;
	const [items, setItems] = useState([]);
	useEffect(() => {
		fetch(`https://jsonplaceholder.ir/users/${userId}`)
			.then((res) => res.json())
			.then((dataFetch) => {
				setItems(dataFetch);
				// localStorage.setItem('table', JSON.stringify(dataFetch));
			});
	}, []);

	const {email, name, avatar, phone, company} = items;

	const userRender = () => {
		return (
			<div>
				<div>
					<img style={{width: '40%', height: '40%'}} src={avatar} alt="" />
				</div>
				<div>
					{name}
					{email}
					{company}
				</div>
				<div>
					<Phone number={phone} />
				</div>
			</div>
		);
	};
	return (
		<div className="row text-center">
			<div className="col">
				<div className="card">{userRender()}</div>
			</div>
		</div>
	);
};

export default UserProfile;
