import React, {useState, useEffect} from 'react';
import userContext from '../../Utility/userContext';
import User from './User';
import Filter from '../../Partials/Filter';
import FilterSign from '../../Partials/Filter/FilterSign';
const Users = (props) => {
	const {match, history, location} = props;
	const url = match.url;
	const search = location.search;
	const [filter, setFilter] = useState('asce');
	const [theme, setTheme] = useState('dark');
	const [items, setItems] = useState([]);
	const query = new URLSearchParams(search);
	const filterType = query.get('id');
	useEffect(() => {
		fetch('https://jsonplaceholder.ir/users')
			.then((res) => res.json())
			.then((dataFetch) => {
				setItems(dataFetch);
				// localStorage.setItem('table', JSON.stringify(dataFetch));
			});
	}, []);
	const userRender = items.map((data) => <User data={data} metaData={props} />);
	const filterHandler = () => setFilter(filterType);
	const showFilter = filter === 'filter' ? <Filter filtering={filterHandler} title={''} /> : <FilterSign filtering={filterHandler} />;

	return (
		<div>
			{theme === 'dark' ? (
				<button onClick={() => setTheme(theme === 'dark' ? 'white' : 'dark')}>
					<i className="material-icons">toggle_off</i>
				</button>
			) : (
				<button onClick={() => setTheme(theme === 'white' ? 'dark' : 'white')}>
					<i className="material-icons">toggle_on</i>
				</button>
			)}

			<button onClick={() => filterType()}>{showFilter}</button>

			<userContext.Provider value={theme}>
				<table className="table table-bordered table-hover table-striped text-center" style={{width: '70%', height: '50%'}}>
					<thead>
						<tr className="dark">
							<td>pics</td>
							<td>name</td>
							<td>link</td>
						</tr>
					</thead>
					<tbody>{userRender}</tbody>
				</table>
			</userContext.Provider>
		</div>
	);
};

export default Users;
