import React, {Component} from 'react';
import Header from './admin/header/Header';
import Tasks from './task/Tasks';
import Courses from './course/Courses';
import AddCourse from './add/AddCourse';
import AddList from './add/AddList';
import Axios from '../Ajax/Axios';
import Loader from './loader/Loader';
import Pannel from './admin/Pannel';

export class App extends Component {
	constructor() {
		super();
		this.state = {
			items: [],
			is_loading: false,
			type: 'list',
			filter: '',
		};
		this.axios = new Axios();
	}

	addItem = (item) => {
		this.setState((prev) => {
			return {
				...prev,
				is_loading: true,
			};
		});
		this.axios
			.post('http://localhost:5000/tasks/add', item)
			.then((res) => {
				this.setState((prev) => {
					return {
						...prev,
						items: [...prev.items, res.data.item],
					};
				});
			})
			.catch((err) => console.log(err))
			.finally(() => {
				this.setState((prev) => {
					return {
						...prev,
						is_loading: false,
					};
				});
			});
	};

	componentHandler = (type) => {
		this.setState({type});
	};
	componentDidMount() {
		this.axios
			.get('http://localhost:5000/tasks/add')
			.then((res) => {
				console.log(res.data.items);
				this.setState({
					items: res.data.items,
				});
			})
			.catch((err) => console.log(err));
	}

	render() {
		const loading = this.state.is_loading ? <Loader /> : null;
		const components = () => {
			switch (this.state.type) {
				case 'addCourse':
					return <AddCourse />;
				case 'addList':
					return <AddList tasksItem={this.addItem} />;
				case 'list':
					return <Tasks tasks={this.state.items} render={this.componentHandler} />;
				case 'course':
					return <Courses render={this.componentHandler} />;
				default:
					break;
			}
		};
		// return (
		// 	<div id="wrapper" className="rtl">
		// 		<div id="container">
		// 			<Header render={this.componentHandler} />
		// 			{loading}
		// 			{components()}
		// 		</div>
		// 	</div>
		// );
		return (
			<div>
				<Pannel />
			</div>
		);
	}
}

export default App;

