import React, {useState, useEffect} from 'react';
import userContext from '../../Utility/userContext';
import User from './User';
import Filter from '../../Partials/Filter';
import FilterSign from '../../Partials/Filter/FilterSign';
const Users = (props) => {
	const {match, history, location} = props;
	const url = match.url;
	const search = location.search;
	const query = new URLSearchParams(search);
	const filterValue = query.get('id');

	const [theme, setTheme] = useState('dark');
	const [items, setItems] = useState([]);
	const [showFilter, setShowFilter] = useState(false);
	useEffect(() => {
		fetch(`https://jsonplaceholder.ir/users`)
			.then((res) => res.json())
			.then((dataFetch) => {
				setItems(dataFetch);
				// localStorage.setItem('table', JSON.stringify(dataFetch));
			});
	}, []);
	useEffect(() => {
		// fetch(`https://jsonplaceholder.ir/users/?id=${filterValue}`)
		// 	.then((res) => res.json())
		// 	.then((dataFetch) => {
		// 		setItems(dataFetch);
		// 	});
		items.sort((a, b) => (a[filterValue] < b[filterValue] ? -1 : 1));
	}, [filterValue]);
	const userRender = items.map((data) => <User data={data} metaData={props} />);
	const filterHandler = () => {
		history.push({
			path: url,
			search: new URLSearchParams({
				id: 3,
			}).toString(),
		});
	};
	const itemsData = [
		{
			whenClick: () => filterHandler(),
			tittle: 'ID',
		},

		{
			whenClick: () => setShowFilter(false),
			tittle: 'X',
		},
	];
	return (
		<div>
			{theme === 'dark' ? (
				<button onClick={() => setTheme(theme === 'dark' ? 'white' : 'dark')}>
					<i className="material-icons">toggle_off</i>
				</button>
			) : (
				<button onClick={() => setTheme(theme === 'white' ? 'dark' : 'white')}>
					<i className="material-icons">toggle_on</i>
				</button>
			)}
			{showFilter ? <Filter items={itemsData} /> : <FilterSign item={() => setShowFilter(true)} />}
			<userContext.Provider value={theme}>
				<table className="table table-bordered table-hover table-striped text-center" style={{width: '70%', height: '50%'}}>
					<thead>
						<tr className="dark">
							<td>pics</td>
							<td>name</td>
							<td>link</td>
						</tr>
					</thead>
					<tbody>{userRender}</tbody>
				</table>
			</userContext.Provider>
		</div>
	);
};

export default Users;
